import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [exhibitions, setExhibitions] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // Search input state
  const [currentPage, setCurrentPage] = useState(1); // Current page state
  const exhibitionsPerPage = 5; // Number of exhibitions per page

  useEffect(() => {
    fetch("http://localhost:5000/exhibitions")
      .then((response) => response.json())
      .then((data) => setExhibitions(data))
      .catch((error) => console.error("Error fetching exhibitions:", error));
  }, []);

  // Filter exhibitions based on search term
  const filteredExhibitions = exhibitions.filter((exhibition) =>
    exhibition.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    exhibition.location.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Pagination Logic
  const indexOfLastExhibition = currentPage * exhibitionsPerPage;
  const indexOfFirstExhibition = indexOfLastExhibition - exhibitionsPerPage;
  const currentExhibitions = filteredExhibitions.slice(indexOfFirstExhibition, indexOfLastExhibition);

  const totalPages = Math.ceil(filteredExhibitions.length / exhibitionsPerPage);

  return (
    <div className="container text-center mt-5">
      <h1 className="display-4 text-primary">Art Exhibition Finder</h1>

      {/* Search Bar */}
      <input
        type="text"
        className="form-control w-50 mx-auto mt-3 mb-4"
        placeholder="Search by name or city..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <h2 className="text-secondary">Upcoming Exhibitions</h2>
      <ul className="list-group w-50 mx-auto">
        {currentExhibitions.length > 0 ? (
          currentExhibitions.map((exhibition) => (
            <li key={exhibition.id} className="list-group-item">
              <strong>{exhibition.name}</strong> <br />
              üìç {exhibition.location} <br />
              üìÖ {exhibition.date}
            </li>
          ))
        ) : (
          <p className="text-danger">No exhibitions found</p>
        )}
      </ul>

      {/* Pagination Buttons */}
      {totalPages > 1 && (
        <div className="mt-4">
          <button
            className="btn btn-outline-primary mx-2"
            onClick={() => setCurrentPage(currentPage - 1)}
            disabled={currentPage === 1}
          >
            ‚óÄ Previous
          </button>

          <span className="mx-2">
            Page {currentPage} of {totalPages}
          </span>

          <button
            className="btn btn-outline-primary mx-2"
            onClick={() => setCurrentPage(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            Next ‚ñ∂
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
